CREATE TABLE `iwebshop_active_data` (
  `time` varchar(40) NOT NULL,
  `rand` varchar(40) NOT NULL,
  `uid` int(20) NOT NULL,
  PRIMARY KEY (`uid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `iwebshop_user` (
  `certificatenum` varchar(32) NOT NULL,
  `tel` varchar(32) NOT NULL,
  `realname` varchar(80) DEFAULT NULL COMMENT '真实姓名',
  `admin_no` varchar(20) DEFAULT NULL,
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `username` varchar(20) NOT NULL COMMENT '用户名',
  `password` char(32) NOT NULL COMMENT '密码',
  `email` varchar(250) DEFAULT NULL COMMENT 'Email',
  `email_verify` tinyint(3) unsigned zerofill DEFAULT '000',
  `head_ico` varchar(250) DEFAULT NULL COMMENT '头像',
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`),
  KEY `email` (`email`)
) ENGINE=MyISAM AUTO_INCREMENT=23 DEFAULT CHARSET=utf8 COMMENT='用户表';

DROP TABLE IF EXISTS `iwebshop_article`;
-- ----------------------------
-- Table structure for iwebshop_article
-- ----------------------------
CREATE TABLE `iwebshop_article` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(250) NOT NULL COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `category_id` int(11) unsigned NOT NULL COMMENT '分类ID',
  `create_time` datetime NOT NULL COMMENT '发布时间',
  `keywords` varchar(255) DEFAULT NULL COMMENT '关键词',
  `description` varchar(255) DEFAULT NULL COMMENT '描述',
  `visibility` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否显示 0:不显示,1:显示',
  `top` tinyint(1) NOT NULL DEFAULT '0' COMMENT '置顶',
  `sort` int(11) NOT NULL DEFAULT '0' COMMENT '排序',
  `style` tinyint(1) DEFAULT '0' COMMENT '标题字体 0正常 1粗体,2斜体',
  `img` varchar(255) DEFAULT NULL,
  `color` varchar(7) DEFAULT NULL COMMENT '标题颜色',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COMMENT='文章表';

